    };
  },
  methods: {
    copyText(shablon, i) {
      this.$refs.textArea[i].select();
      window.navigator.clipboard.writeText(shablon);
    },
  },
  computed: {
    searchEngine() {
      return this.shablons.filter((item) => item.indexOf(this.search) !== -1);
    },
  },
};
</script>

